import React from "react";
import { Redirect } from "react-router-dom";
import "./styles.css";
import Navbar from "../Navbar";
import AdminDashboard from "./Admin/admin_dash";
import ClientDashboard from "./client_dash";
import RampInfoPage from "./RampInfoPage";

/* Primary Component for the Admin Dashboard page */
class Dashboard extends React.Component {
    


  constructor(props) {
    super(props);
    console.log(this.props)

    const name = this.props.username
    const id = this.props.getID(name)
    console.log(id)


    


    
    this.state = {
      pageStack: [], // when we navigate to a "sub-page", push to stack. When we "go back", pop from stack
      isLoaded: false, // 
      id:id
    }
  }

  pushToStack = (component) => {
    const newStack = [... this.state.pageStack];
    newStack.push(component);
    this.setState({
      pageStack: newStack
    });
  }

  goBack = () => {
    const newStack = [... this.state.pageStack];
    newStack.pop();
    this.setState({
      pageStack: newStack
    });
  }

  gotoFuncs = {
    ramp_info: (isAdmin,data) => this.pushToStack(<RampInfoPage goBack = {this.goBack} data = {data} isAdmin={isAdmin} />)
  }

  // getID() {
  //   fetch("/users", {
  //     method: 'GET',
  //     headers: {
  //       'Accept': 'application/json',
  //       'Authorization': `Bearer ${localStorage.getItem('token-access')}`
  //     }
  //   })
  //   .then(res => {
  //     if (res.ok)
  //       return res.json()
  //     throw new Error(`Something went wrong with error code ${res.status}`)
  //   })
  //   .then(
  //     (result) => {
  //       var id 

  //       const users = result["results"];
  //       users.map(x => {
  //         if (x["username"] == this.props.username){
  //             id = x["pk"]

              
  //         }
  //       })

  //       this.setState({
  //         isLoaded: true,
  //         id:id
  //       });
      
  //     },
  //     // Note: it's important to handle errors here
  //     // instead of a catch() block so that we don't swallow
  //     // exceptions from actual bugs in components.
  //     (error) => {
  //       this.setState({
  //         isLoaded: true,
  //         error
  //       });
       
  //     }
  //   )

  

  // }

  


  
  

  render () {
    if (!this.props.loggedIn) // user is not logged in.
      return <Redirect to="/" /> 


    
    console.log(this.state)


    
    return (
      <div>
        <Navbar title={this.props.isAdmin ? "Admin Dashboard" : "Client Dashboard"} logout={this.props.logout} goBack={this.goBack} stackSize={this.state.pageStack.length}/>
        <div className={"content"}>
          {this.state.pageStack.length == 0 ? 
            this.props.isAdmin 
              ? <AdminDashboard user = {this.state.user} logout={this.props.logout} gotoFuncs={this.gotoFuncs} /> 
              : <ClientDashboard user = {this.state.id} l logout={this.props.logout}  gotoFuncs={this.gotoFuncs} /> :
            this.state.pageStack[this.state.pageStack.length - 1]
          }
        </div>
      </div>
    );
  }
}


export default Dashboard;
